import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom'; // useNavigate вместо useHistory

const BookForm = () => {
    const [book, setBook] = useState({
        title: '',
        author: '',
        genre: '',
        description: '',
        price: '',
        stock: '',
        coverImage: '',
        publishedYear: '',
        publisher: ''
    });

    const { id } = useParams();
    const navigate = useNavigate(); // Используем useNavigate вместо useHistory

    useEffect(() => {
        if (id) {
            // Если id передан в URL, это редактирование
            axios.get(`http://localhost:5000/api/books/${id}`)
                .then(response => setBook(response.data))
                .catch(error => console.error('Error fetching book:', error));
        }
    }, [id]);

    const handleChange = (e) => {
        const { name, value } = e.target;
        setBook(prevState => ({ ...prevState, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();

        const token = localStorage.getItem('token');
        const headers = { Authorization: `Bearer ${token}` };

        if (id) {
            axios.put(`http://localhost:5000/api/books/${id}`, book, { headers })
                .then(() => navigate('/')) // Используем navigate
                .catch(error => console.error('Error updating book:', error));
        } else {
            axios.post('http://localhost:5000/api/books', book, { headers })
                .then(() => navigate('/')) // Используем navigate
                .catch(error => console.error('Error creating book:', error));
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <h1>{id ? 'Edit Book' : 'Add Book'}</h1>
            <input type="text" name="title" value={book.title} onChange={handleChange} placeholder="Title" required />
            <input type="text" name="author" value={book.author} onChange={handleChange} placeholder="Author" required />
            <textarea name="description" value={book.description} onChange={handleChange} placeholder="Description"></textarea>
            <input type="number" name="price" value={book.price} onChange={handleChange} placeholder="Price" required />
            <input type="number" name="stock" value={book.stock} onChange={handleChange} placeholder="Stock" required />
            <input type="text" name="coverImage" value={book.coverImage} onChange={handleChange} placeholder="Cover Image URL" />
            <input type="number" name="publishedYear" value={book.publishedYear} onChange={handleChange} placeholder="Published Year" />
            <input type="text" name="publisher" value={book.publisher} onChange={handleChange} placeholder="Publisher" />
            <button type="submit">{id ? 'Update Book' : 'Add Book'}</button>
        </form>
    );
};

export default BookForm;
