// OrderPage.js (React)
import React, { useState } from 'react';
import axios from 'axios';

function OrderPage() {
    const [shippingAddress, setShippingAddress] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('');
    const [error, setError] = useState(null);
    const [orderSuccess, setOrderSuccess] = useState(false);

   const handleSubmit = (e) => {
    e.preventDefault();

    const token = localStorage.getItem('jwt');
    const orderData = {
        items: cartItems.map(item => ({
            book: item.book._id,
            quantity: item.quantity
        })),
        totalPrice: cartItems.reduce((total, item) => total + item.book.price * item.quantity, 0)
    };

    fetch('http://localhost:5000/api/orders', {
        method: 'POST',
        headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderData),
    })
    .then(response => {
        if (!response.ok) {
            throw new Error('Ошибка оформления заказа');
        }
        return response.json();
    })
    .then(data => {
        console.log('Заказ оформлен:', data);
        // Возможно, хотите перенаправить на страницу с подтверждением
    })
    .catch(err => {
        console.error('Ошибка:', err);
        alert('Ошибка оформления заказа');
    });
};


    return (
        <div>
            <h1>Оформление заказа</h1>
            {error && <p>{error}</p>}
            {orderSuccess && <p>Заказ успешно оформлен!</p>}
            <form onSubmit={handleSubmit}>
                <label>
                    Адрес доставки:
                    <input 
                        type="text" 
                        value={shippingAddress} 
                        onChange={(e) => setShippingAddress(e.target.value)} 
                    />
                </label>
                <br />
                <label>
                    Способ оплаты:
                    <input 
                        type="text" 
                        value={paymentMethod} 
                        onChange={(e) => setPaymentMethod(e.target.value)} 
                    />
                </label>
                <br />
                <button type="submit">Оформить заказ</button>
            </form>
        </div>
    );
}

export default OrderPage;
