import React from 'react';
import './Home.css';

const Home = () => {
    const currentDate = new Date();
    
    // Форматируем текущую дату для пользователя (с учетом тайм-зоны)
    const formattedDate = currentDate.toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });

    // Форматируем текущее время для пользователя
    const formattedTime = currentDate.toLocaleTimeString(undefined, {
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });

    // Форматируем дату и время для UTC
    const utcFormattedDate = currentDate.toISOString(); // Время в формате UTC (ISO 8601)

    // Получаем тайм-зону пользователя
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    console.log('Home component loaded');
    
    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>
            <p>Discover genres from fiction to non-fiction, and everything in between.</p>
            <div className="date-time-info">
                <p><strong>Current Date (User's Timezone):</strong> {formattedDate}</p>
                <p><strong>Current Time (User's Timezone):</strong> {formattedTime}</p>
                <p><strong>Time Zone:</strong> {timeZone}</p>
                <p><strong>Data Update (User's Timezone):</strong> {formattedDate} {formattedTime}</p>
                <p><strong>Data Update (UTC):</strong> {utcFormattedDate}</p>
            </div>
        </div>
    );
};

export default Home;
