import React, { useEffect, useState } from 'react';
import './Home.css';

const Home = () => {
    const [weatherData, setWeatherData] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const fetchWeatherData = async () => {
        try {
            const response = await fetch(
                'https://api.gismeteo.net/v2/weather/current/?latitude=54.35&longitude=52.52',
                {
                    headers: {
                        'X-Gismeteo-Token': '56b30cb255.3443075',
                        'Accept-Encoding': 'deflate, gzip',
                    },
                }
            );
            if (!response.ok) {
                throw new Error('Failed to fetch weather data');
            }
            const data = await response.json();
            setWeatherData(data);
            setLoading(false);
        } catch (error) {
            setError(error.message);
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchWeatherData();
    }, []);

    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>
            <p>Discover genres from fiction to non-fiction, and everything in between.</p>
            <div className="date-time-info">
                <p><strong>Current Date:</strong> {formattedDate}</p>
                <p><strong>Time Zone:</strong> {timeZone}</p>
            </div>
            <div className="weather-info">
                {loading && <p>Loading weather data...</p>}
                {error && <p>Error: {error}</p>}
                {weatherData && (
                    <>
                        <h3>Current Weather</h3>
                        <p><strong>Description:</strong> {weatherData.description.full}</p>
                        <p><strong>Temperature:</strong> {weatherData.temperature.air.C} Â°C</p>
                        <p><strong>Humidity:</strong> {weatherData.humidity.percent}%</p>
                        <p><strong>Pressure:</strong> {weatherData.pressure.mm_hg_atm} mmHg</p>
                        <p><strong>Cloudiness:</strong> {weatherData.cloudiness.percent}%</p>
                    </>
                )}
            </div>
        </div>
    );
};

export default Home;
