import React, { useState, useEffect } from 'react';
import './WeatherComponent.css';

const WeatherComponent = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchWeatherData = async () => {
      const lat = 44.34; 
      const lon = 10.99;
      const apiKey = 'd2070668968af3232bd18d95f86bc26c'; 

      try {
        const response = await fetch(`https://api.openweathermap.org/data/2.5/weather?lat=${lat}&lon=${lon}&appid=${apiKey}&units=metric&lang=en`);
        
        if (!response.ok) {
          throw new Error('Error fetching weather data');
        }
        
        const data = await response.json();
        setWeatherData(data);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchWeatherData();
  }, []);

  return (
    <div className="weather-container">
      {error && <p className="error-message">Error: {error}</p>}
      {weatherData ? (
        <div>
          <h3 className="weather-header">{weatherData.name}, {weatherData.sys.country}</h3>
          <img
            className="weather-icon"
            src={`http://openweathermap.org/img/wn/${weatherData.weather[0].icon}@2x.png`}
            alt="Weather Icon"
          />
          <div className="weather-details">
            <p>Temperature: {weatherData.main.temp}Â°C</p>
            <p>Weather: {weatherData.weather[0].description}</p>
            <p>Humidity: {weatherData.main.humidity}%</p>
            <p>Wind: {weatherData.wind.speed} m/s</p>
          </div>
        </div>
      ) : (
        <p className="loading-message">Loading weather data...</p>
      )}
    </div>
  );
};

export default WeatherComponent;
