import React, { useEffect, useState } from 'react';
import './Home.css';

const Home = () => {
    const [weatherData, setWeatherData] = useState([]); // Состояние для данных о погоде
    const [error, setError] = useState(null); // Состояние для ошибок
    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                // Запрос на сервер Express
                const response = await fetch('/api/weather?query=moscow');

                if (!response.ok) {
                    throw new Error('Error fetching weather data');
                }

                const data = await response.json();
                setWeatherData(data || []); // Сохраняем данные
            } catch (err) {
                setError(err.message); // Сохраняем сообщение об ошибке
            }
        };

        fetchWeatherData();
    }, []);

    console.log('Home component loaded');

    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>
            <p>Discover genres from fiction to non-fiction, and everything in between.</p>
            <div className="date-time-info">
                <p><strong>Current Date:</strong> {formattedDate}</p>
                <p><strong>Time Zone:</strong> {timeZone}</p>
            </div>
            <div className="weather-info">
                <h3>Weather Information</h3>
                {error ? (
                    <p className="error">Error: {error}</p>
                ) : weatherData.length > 0 ? (
                    <ul>
                        {weatherData.map((city) => (
                            <li key={city.id}>
                                <strong>City:</strong> {city.name}, {city.country?.name || 'Unknown Country'}
                                <br />
                                <strong>Region:</strong> {city.district?.name || 'N/A'}
                                <br />
                                <a
                                    href={`https://www.gismeteo.com${city.url}`}
                                    target="_blank"
                                    rel="noopener noreferrer"
                                >
                                    View Weather
                                </a>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>Loading weather data...</p>
                )}
            </div>
        </div>
    );
};

export default Home;
