import React from 'react';
import './Home.css';

const Home = () => {
    // Пример данных книги, включая дату добавления
    const book = {
        title: 'The Great Gatsby', // Название книги
        createdAt: '2024-12-03T10:00:00Z', // Дата добавления книги в формате ISO (UTC)
    };

    // Преобразуем строку даты (ISO) в объект Date
    const createdDate = new Date(book.createdAt);

    // Форматируем дату для пользовательской тайм-зоны
    const userTimeFormatted = createdDate.toLocaleString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });

    // Форматируем дату в формате UTC
    const utcFormatted = createdDate.toISOString(); // ISO формат UTC

    console.log('Home component loaded');
    
    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>
            <p>Discover genres from fiction to non-fiction, and everything in between.</p>

            <div className="book-info">
                <h3>Book Information:</h3>
                <p><strong>Book Title:</strong> {book.title}</p>
                <p><strong>Added on (User's Timezone):</strong> {userTimeFormatted}</p>
                <p><strong>Added on (UTC):</strong> {utcFormatted}</p>
            </div>
        </div>
    );
};

export default Home;
