import React, { useEffect, useState } from 'react';
import './Home.css';

const Home = () => {
    const [weatherData, setWeatherData] = useState(null); // Состояние для данных о погоде
    const [error, setError] = useState(null); // Состояние для ошибок
    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    useEffect(() => {
        const fetchWeatherData = async () => {
            try {
                console.log("Fetching weather data...");

                // Запрос на сервер Express
                const response = await fetch('/api/weather?query=moscow'); // Здесь передаем запрос с названием города

                console.log("Response status:", response.status);

                if (!response.ok) {
                    throw new Error('Error fetching weather data');
                }

                const data = await response.json();
                setWeatherData(data); // Сохраняем данные о погоде
            } catch (err) {
                console.error("Error while fetching weather data:", err.message);
                setError(err.message); // Сохраняем ошибку
            }
        };

        fetchWeatherData();
    }, []);

    console.log('Home component rendered');

    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>
            <p>Discover genres from fiction to non-fiction, and everything in between.</p>
            <div className="date-time-info">
                <p><strong>Current Date:</strong> {formattedDate}</p>
                <p><strong>Time Zone:</strong> {timeZone}</p>
            </div>
            <div className="weather-info">
                <h3>Weather Information</h3>
                {error ? (
                    <p className="error">Error: {error}</p>
                ) : weatherData ? (
                    <div>
                        <p><strong>City:</strong> {weatherData.name}, {weatherData.sys.country}</p>
                        <p><strong>Temperature:</strong> {weatherData.main.temp}°C</p>
                        <p><strong>Weather:</strong> {weatherData.weather[0].description}</p>
                        <p><strong>Humidity:</strong> {weatherData.main.humidity}%</p>
                        <p><strong>Wind:</strong> {weatherData.wind.speed} m/s</p>
                    </div>
                ) : (
                    <p>Loading weather data...</p>
                )}
            </div>
        </div>
    );
};

export default Home;
