import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom'; // Для получения параметров маршрута
import './BookDetail.css';

const BookDetail = () => {
    const { bookId } = useParams(); // Получаем параметр ID книги из URL
    const [book, setBook] = useState(null);
    const [error, setError] = useState(null);
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Проверка авторизации
    const [isInCart, setIsInCart] = useState(false); // Проверка, добавлен ли товар в корзину
    const [reviews, setReviews] = useState([]); // Состояние для хранения отзывов
    const [rating, setRating] = useState(1); // Состояние для рейтинга отзыва
    const [comment, setComment] = useState(''); // Состояние для комментария отзыва
    const [submitError, setSubmitError] = useState(null); // Для ошибок при отправке отзыва

    useEffect(() => {
        // Проверка авторизации (например, по токену в localStorage)
        const token = localStorage.getItem('jwt');
        setIsAuthenticated(!!token); // Если токен есть, пользователь авторизован

        // Загружаем данные о книге
        fetch(`http://localhost:5000/api/books/${bookId}`) // API для получения данных о книге по ID
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Ошибка загрузки данных');
                }
                return response.json();
            })
            .then((data) => setBook(data))
            .catch((err) => setError(err.message));

        // Загружаем отзывы для этой книги
        fetch(`http://localhost:5000/api/reviews/book/${bookId}`)
            .then((response) => response.json())
            .then((data) => setReviews(data))
            .catch((err) => console.error('Ошибка загрузки отзывов:', err));
    }, [bookId]);

    const handleAddToCart = () => {
        if (!isAuthenticated) {
            alert('Для добавления в корзину необходимо авторизоваться');
            return;
        }

        // Отправляем запрос на добавление товара в корзину
        const token = localStorage.getItem('jwt');
        fetch('http://localhost:5000/api/cart', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ bookId, quantity: 1 }), // Добавляем одну книгу
        })
        .then((response) => {
            if (response.ok) {
                setIsInCart(true); // Если товар успешно добавлен в корзину
                alert('Товар добавлен в корзину!');
            } else {
                alert('Ошибка при добавлении товара в корзину');
            }
        })
        .catch((error) => {
            console.error('Ошибка при добавлении товара в корзину:', error);
            alert('Ошибка при добавлении товара в корзину');
        });
    };

    const handleSubmitReview = (e) => {
        e.preventDefault();

        if (!rating || !comment) {
            setSubmitError('Пожалуйста, заполните все поля!');
            return;
        }

        const token = localStorage.getItem('jwt');
        if (!token) {
            setSubmitError('Для отправки отзыва необходимо авторизоваться');
            return;
        }

        // Отправка отзыва на сервер
        fetch('http://localhost:5000/api/reviews', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                Authorization: `Bearer ${token}`,
            },
            body: JSON.stringify({ book: bookId, rating, comment }),
        })
        .then((response) => {
            if (response.ok) {
                setSubmitError(null);
                alert('Отзыв успешно отправлен!');
                // Перезагружаем отзывы
                return fetch(`http://localhost:5000/api/reviews/book/${bookId}`);
            } else {
                return response.json().then((data) => {
                    setSubmitError(data.message || 'Ошибка при отправке отзыва');
                });
            }
        })
        .then((data) => setReviews(data))
        .catch((err) => {
            console.error('Ошибка при отправке отзыва:', err);
            setSubmitError('Ошибка при отправке отзыва');
        });
    };

    if (error) {
        return <p className="error">{error}</p>;
    }

    if (!book) {
        return <p>Загрузка...</p>;
    }

    return (
        <div className="book-detail">
            <h1>{book.title}</h1>
            <p><strong>Автор:</strong> {book.author}</p>
            <p><strong>Жанр:</strong> {book.genre}</p>
            <p><strong>Описание:</strong> {book.description}</p>
            <p><strong>Цена:</strong> {book.price} руб.</p>
            <p><strong>Количество в наличии:</strong> {book.stock}</p>

            {isAuthenticated && !isInCart && (
                <button onClick={handleAddToCart} className="btn-buy">Купить</button>
            )}

            {isInCart && <p>Товар уже в корзине</p>}

            <h2>Отзывы</h2>
            {reviews.length === 0 ? (
                <p>Нет отзывов для этой книги.</p>
            ) : (
                <ul>
                    {reviews.map((review) => (
                        <li key={review._id}>
                            <p><strong>Рейтинг:</strong> {review.rating} / 5</p>
                            <p>{review.comment}</p>
                            <p><strong>Автор отзыва:</strong> {review.user.email}</p>
                        </li>
                    ))}
                </ul>
            )}

            {isAuthenticated && (
                <div className="review-form">
                    <h3>Оставить отзыв</h3>
                    {submitError && <p className="error">{submitError}</p>}
                    <form onSubmit={handleSubmitReview}>
                        <label>
                            Рейтинг (1-5):
                            <input 
                                type="number" 
                                value={rating} 
                                onChange={(e) => setRating(e.target.value)} 
                                min="1" max="5" 
                            />
                        </label>
                        <br />
                        <label>
                            Комментарий:
                            <textarea 
                                value={comment} 
                                onChange={(e) => setComment(e.target.value)} 
                            />
                        </label>
                        <br />
                        <button type="submit">Отправить отзыв</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default BookDetail;
