import React, { useEffect, useState } from 'react';
import axios from 'axios';

const BookList = () => {
    const [books, setBooks] = useState([]);
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    // Функция для получения списка книг
    useEffect(() => {
        axios.get('http://localhost:5000/api/books')
            .then(response => {
                setBooks(response.data);
            })
            .catch(error => {
                console.error(error);
            });

        // Проверка авторизации (например, наличие токена в localStorage)
        const token = localStorage.getItem('token');
        if (token) {
            setIsAuthenticated(true);
        }
    }, []);

    const deleteBook = (id) => {
        axios.delete(`http://localhost:5000/api/books/${id}`, {
            headers: { Authorization: `Bearer ${localStorage.getItem('token')}` }
        })
        .then(response => {
            setBooks(books.filter(book => book._id !== id));
        })
        .catch(error => {
            console.error('Error deleting book:', error);
        });
    };

    return (
        <div>
            <h1>Book List</h1>
            {isAuthenticated && (
                <div>
                    <button onClick={() => window.location.href = '/add'}>Add New Book</button>
                </div>
            )}
            <ul>
                {books.map(book => (
                    <li key={book._id}>
                        <h3>{book.title} by {book.author}</h3>
                        <p>{book.genre}</p>
                        <p>{book.description}</p>
                        {isAuthenticated && (
                            <div>
                                <button onClick={() => window.location.href = `/edit/${book._id}`}>Edit</button>
                                <button onClick={() => deleteBook(book._id)}>Delete</button>
                            </div>
                        )}
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default BookList;
