import React, { useEffect, useState } from 'react';
import axios from 'axios';

function OrderPage() {
    const [shippingAddress, setShippingAddress] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('');
    const [cartItems, setCartItems] = useState([]);
    const [error, setError] = useState(null);
    const [orderSuccess, setOrderSuccess] = useState(false);
    const [orders, setOrders] = useState([]); // Состояние для заказов
    const [reviews, setReviews] = useState({}); // Состояние для отзывов

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        console.log('Токен из localStorage:', token);

        if (!token) {
            setError('Для оформления заказа необходимо авторизоваться');
            return;
        }

        // Получение корзины пользователя
        axios.get('http://localhost:5000/api/cart', {
            headers: { 'Authorization': `Bearer ${token}` }
        })
        .then((response) => {
            setCartItems(response.data.items);
        })
        .catch((err) => {
            setError('Ошибка при загрузке корзины');
        });

        // Получение всех заказов пользователя
        axios.get('http://localhost:5000/api/orders', {
            headers: { 'Authorization': `Bearer ${token}` }
        })
        .then((response) => {
            setOrders(response.data);
        })
        .catch((err) => {
            setError('Ошибка при загрузке заказов');
        });
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();

        if (!shippingAddress || !paymentMethod) {
            setError('Пожалуйста, заполните все поля!');
            return;
        }

        const token = localStorage.getItem('jwt');
        if (cartItems.length === 0) {
            setError('Корзина пуста!');
            return;
        }

        const orderData = {
            shippingAddress,
            paymentMethod,
            items: cartItems.map(item => ({
                book: item.book._id, 
                quantity: item.quantity
            })),
            totalPrice: cartItems.reduce((total, item) => total + item.book.price * item.quantity, 0),
        };

        axios.post('http://localhost:5000/api/orders', orderData, {
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json',
            },
        })
        .then((response) => {
            setOrderSuccess(true);
            setShippingAddress('');
            setPaymentMethod('');
            setCartItems([]);
        })
        .catch((err) => {
            setError('Ошибка оформления заказа');
        });
    };

    const handleReviewChange = (orderId, bookId, e) => {
        const { name, value } = e.target;
        setReviews(prevReviews => ({
            ...prevReviews,
            [orderId]: {
                ...prevReviews[orderId],
                [bookId]: {
                    ...prevReviews[orderId]?.[bookId],
                    [name]: value
                }
            }
        }));
    };

    const handleSubmitReview = (orderId, bookId) => {
        const reviewData = reviews[orderId]?.[bookId];
        if (!reviewData?.rating || !reviewData?.comment) {
            setError('Пожалуйста, заполните все поля отзыва');
            return;
        }

        const token = localStorage.getItem('jwt');
        const reviewPayload = {
            book: bookId,
            rating: reviewData.rating,
            comment: reviewData.comment,
        };

        axios.post('http://localhost:5000/api/reviews', reviewPayload, {
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json',
            },
        })
        .then((response) => {
            alert('Отзыв оставлен');
            setReviews(prevReviews => ({
                ...prevReviews,
                [orderId]: {
                    ...prevReviews[orderId],
                    [bookId]: { rating: '', comment: '' } // Очищаем форму отзыва
                }
            }));
        })
        .catch((err) => {
            setError('Ошибка при отправке отзыва');
        });
    };

    return (
        <div>
            <h1>Оформление заказа</h1>
            {error && <p>{error}</p>}
            {orderSuccess && <p>Заказ успешно оформлен!</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Адрес доставки:
                    <input 
                        type="text" 
                        value={shippingAddress} 
                        onChange={(e) => setShippingAddress(e.target.value)} 
                    />
                </label>
                <br />
                <label>
                    Способ оплаты:
                    <input 
                        type="text" 
                        value={paymentMethod} 
                        onChange={(e) => setPaymentMethod(e.target.value)} 
                    />
                </label>
                <br />
                <button type="submit">Оформить заказ</button>
            </form>

            <h2>Корзина</h2>
            {cartItems.length === 0 ? (
                <p>Корзина пуста.</p>
            ) : (
                <ul>
                    {cartItems.map((item) => (
                        <li key={item.book._id}>
                            <p>{item.book.title}</p>
                            <p>Количество: {item.quantity}</p>
                            <p>Цена: {item.book.price} руб.</p>
                        </li>
                    ))}
                </ul>
            )}

            <h2>Мои заказы</h2>
            {orders.length === 0 ? (
                <p>У вас нет заказов.</p>
            ) : (
                <ul>
                    {orders.map((order) => (
                        <li key={order._id}>
                            <p><strong>Заказ №{order._id}</strong></p>
                            <p>Адрес доставки: {order.shippingAddress}</p>
                            <p>Способ оплаты: {order.paymentMethod}</p>
                            <p>Общая стоимость: {order.totalPrice} руб.</p>
                            <p>Статус: {order.status}</p>
                            <ul>
                                {order.items.map((item) => (
                                    <li key={item.book._id}>
                                        <p>{item.book.title} (x{item.quantity})</p>
                                        <div>
                                            <label>Оценка (1-5):</label>
                                            <input 
                                                type="number" 
                                                name="rating" 
                                                value={reviews[order._id]?.[item.book._id]?.rating || ''} 
                                                onChange={(e) => handleReviewChange(order._id, item.book._id, e)}
                                                min="1" 
                                                max="5"
                                            />
                                        </div>
                                        <div>
                                            <label>Комментарий:</label>
                                            <textarea
                                                name="comment"
                                                value={reviews[order._id]?.[item.book._id]?.comment || ''}
                                                onChange={(e) => handleReviewChange(order._id, item.book._id, e)}
                                            />
                                        </div>
                                        <button 
                                            onClick={() => handleSubmitReview(order._id, item.book._id)}
                                        >
                                            Оставить отзыв
                                        </button>
                                    </li>
                                ))}
                            </ul>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default OrderPage;
