import React, { useEffect, useState } from 'react';
import axios from 'axios';

function OrderPage() {
    const [shippingAddress, setShippingAddress] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('');
    const [cartItems, setCartItems] = useState([]);
    const [error, setError] = useState(null);
    const [orderSuccess, setOrderSuccess] = useState(false);
    const [orders, setOrders] = useState([]); // Состояние для заказов

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        console.log('Токен из localStorage:', token);

        if (!token) {
            setError('Для оформления заказа необходимо авторизоваться');
            console.log('Ошибка: нет токена');
            return;
        }

        // Получение корзины пользователя
        axios.get('http://localhost:5000/api/cart', {
            headers: { 'Authorization': `Bearer ${token}` }
        })
        .then((response) => {
            console.log('Данные корзины загружены:', response.data);
            setCartItems(response.data.items);
        })
        .catch((err) => {
            setError('Ошибка при загрузке корзины');
            console.error('Ошибка при загрузке корзины:', err);
        });

        // Получение всех заказов пользователя
        axios.get('http://localhost:5000/api/orders', {
            headers: { 'Authorization': `Bearer ${token}` }
        })
        .then((response) => {
            console.log('Данные заказов пользователя:', response.data);
            setOrders(response.data);
        })
        .catch((err) => {
            console.error('Ошибка при загрузке заказов:', err);
            setError('Ошибка при загрузке заказов');
        });
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log('Обработка формы...');

        if (!shippingAddress || !paymentMethod) {
            setError('Пожалуйста, заполните все поля!');
            console.log('Ошибка: поля не заполнены');
            return;
        }

        const token = localStorage.getItem('jwt');
        console.log('Токен для отправки заказа:', token);

        if (cartItems.length === 0) {
            setError('Корзина пуста!');
            console.log('Ошибка: корзина пуста');
            return;
        }

        console.log("Данные корзины перед отправкой: ", cartItems);

        const orderData = {
            shippingAddress,
            paymentMethod,
            items: cartItems.map(item => ({
                book: item.book._id, 
                quantity: item.quantity
            })),
            totalPrice: cartItems.reduce((total, item) => total + item.book.price * item.quantity, 0),
        };

        console.log("Данные для отправки на сервер: ", orderData);

        axios.post('http://localhost:5000/api/orders', orderData, {
            headers: {
                'Authorization': `Bearer ${token}`,
                'Content-Type': 'application/json',
            },
        })
        .then((response) => {
            console.log('Ответ от сервера:', response.data);
            setOrderSuccess(true);
            setShippingAddress('');
            setPaymentMethod('');
            setCartItems([]);
        })
        .catch((err) => {
            console.error('Ошибка при оформлении заказа:', err);
            if (err.response && err.response.data) {
                setError(err.response.data.message || 'Ошибка оформления заказа');
                console.log('Сообщение об ошибке от сервера:', err.response.data.message);
            } else {
                setError('Ошибка оформления заказа');
                console.log('Общая ошибка оформления заказа');
            }
        });
    };

    return (
        <div>
            <h1>Оформление заказа</h1>
            {error && <p>{error}</p>}
            {orderSuccess && <p>Заказ успешно оформлен!</p>}

            <form onSubmit={handleSubmit}>
                <label>
                    Адрес доставки:
                    <input 
                        type="text" 
                        value={shippingAddress} 
                        onChange={(e) => setShippingAddress(e.target.value)} 
                    />
                </label>
                <br />
                <label>
                    Способ оплаты:
                    <input 
                        type="text" 
                        value={paymentMethod} 
                        onChange={(e) => setPaymentMethod(e.target.value)} 
                    />
                </label>
                <br />
                <button type="submit">Оформить заказ</button>
            </form>

            <h2>Корзина</h2>
            {cartItems.length === 0 ? (
                <p>Корзина пуста.</p>
            ) : (
                <ul>
                    {cartItems.map((item) => (
                        <li key={item.book._id}>
                            <p>{item.book.title}</p>
                            <p>Количество: {item.quantity}</p>
                            <p>Цена: {item.book.price} руб.</p>
                        </li>
                    ))}
                </ul>
            )}

            <h2>Мои заказы</h2>
            {orders.length === 0 ? (
                <p>У вас нет заказов.</p>
            ) : (
                <ul>
                    {orders.map((order) => (
                        <li key={order._id}>
                            <p><strong>Заказ №{order._id}</strong></p>
                            <p>Адрес доставки: {order.shippingAddress}</p>
                            <p>Способ оплаты: {order.paymentMethod}</p>
                            <p>Общая стоимость: {order.totalPrice} руб.</p>
                            <p>Статус: {order.status}</p>
                            <ul>
                                {order.items.map((item) => (
                                    <li key={item.book._id}>
                                        <p>{item.book.title} (x{item.quantity})</p>
                                    </li>
                                ))}
                            </ul>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
}

export default OrderPage;
