import React, { useEffect, useState } from 'react';
import axios from 'axios';
import styles from './OrderPage.module.css'; // Импорт модуля стилей

function OrderPage() {
    const [shippingAddress, setShippingAddress] = useState('');
    const [paymentMethod, setPaymentMethod] = useState('');
    const [cartItems, setCartItems] = useState([]);
    const [error, setError] = useState(null);
    const [orderSuccess, setOrderSuccess] = useState(false);
    const [orders, setOrders] = useState([]);
    const [reviews, setReviews] = useState({});

    useEffect(() => {
        const token = localStorage.getItem('jwt');
        if (!token) {
            setError('Для оформления заказа необходимо авторизоваться');
            return;
        }

        axios.get('http://localhost:5000/api/cart', {
            headers: { 'Authorization': `Bearer ${token}` },
        }).then((response) => setCartItems(response.data.items))
          .catch(() => setError('Ошибка при загрузке корзины'));

        axios.get('http://localhost:5000/api/orders', {
            headers: { 'Authorization': `Bearer ${token}` },
        }).then((response) => setOrders(response.data))
          .catch(() => setError('Ошибка при загрузке заказов'));
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!shippingAddress || !paymentMethod) {
            setError('Пожалуйста, заполните все поля!');
            return;
        }

        const token = localStorage.getItem('jwt');
        if (cartItems.length === 0) {
            setError('Корзина пуста!');
            return;
        }

        const orderData = {
            shippingAddress,
            paymentMethod,
            items: cartItems.map(item => ({ book: item.book._id, quantity: item.quantity })),
            totalPrice: cartItems.reduce((total, item) => total + item.book.price * item.quantity, 0),
        };

        axios.post('http://localhost:5000/api/orders', orderData, {
            headers: { 'Authorization': `Bearer ${token}`, 'Content-Type': 'application/json' },
        }).then(() => {
            setOrderSuccess(true);
            setShippingAddress('');
            setPaymentMethod('');
            setCartItems([]);
        }).catch(() => setError('Ошибка оформления заказа'));
    };

    return (
        <div className={styles['order-page']}>
            <div className={styles['order-container']}>
                <h1 className={styles['order-header']}>Оформление заказа</h1>
                {error && <p className={styles['error-message']}>{error}</p>}
                {orderSuccess && <p className={styles['success-message']}>Заказ успешно оформлен!</p>}
                <form className={styles['order-form']} onSubmit={handleSubmit}>
                    <label>
                        Адрес доставки:
                        <input type="text" value={shippingAddress} onChange={(e) => setShippingAddress(e.target.value)} />
                    </label>
                    <label>
                        Способ оплаты:
                        <input type="text" value={paymentMethod} onChange={(e) => setPaymentMethod(e.target.value)} />
                    </label>
                    <button type="submit">Оформить заказ</button>
                </form>
            </div>
        </div>
    );
}

export default OrderPage;
