import React from 'react';
import './Home.css';

const Home = () => {
    // Пример данных книги с датой добавления
    const book = {
        title: 'The Great Gatsby', // Название книги
        createdAt: '2024-12-03T10:00:00Z', // Дата добавления книги в формате ISO (UTC)
        updatedAt: '2024-12-04T15:00:00Z' // Дата последнего обновления книги (UTC)
    };

    // Получаем текущую дату
    const currentDate = new Date();

    // Форматируем текущую дату в тайм-зоне пользователя
    const userTimeFormatted = currentDate.toLocaleString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });

    // Форматируем текущую дату в UTC
    const utcFormatted = currentDate.toISOString(); // ISO формат UTC

    // Форматируем дату добавления книги в тайм-зоне пользователя
    const createdDate = new Date(book.createdAt);
    const userCreatedTime = createdDate.toLocaleString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });

    // Форматируем дату добавления книги в UTC
    const utcCreatedTime = createdDate.toISOString(); // ISO формат UTC

    // Форматируем дату последнего обновления книги в тайм-зоне пользователя
    const updatedDate = new Date(book.updatedAt);
    const userUpdatedTime = updatedDate.toLocaleString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit',
        second: '2-digit',
    });

    // Форматируем дату последнего обновления книги в UTC
    const utcUpdatedTime = updatedDate.toISOString(); // ISO формат UTC

    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>

            <div className="date-time-info">
                <h3>Current Date:</h3>
                <p><strong>In Your Timezone:</strong> {userTimeFormatted}</p>
                <p><strong>In UTC:</strong> {utcFormatted}</p>
            </div>

            <div className="book-info">
                <h3>Book Information:</h3>
                <p><strong>Book Title:</strong> {book.title}</p>
                <p><strong>Added on (User's Timezone):</strong> {userCreatedTime}</p>
                <p><strong>Added on (UTC):</strong> {utcCreatedTime}</p>
                <p><strong>Updated on (User's Timezone):</strong> {userUpdatedTime}</p>
                <p><strong>Updated on (UTC):</strong> {utcUpdatedTime}</p>
            </div>
        </div>
    );
};

export default Home;
