import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom'; // Импортируем Link для перехода
import './Catalog.css';

const Catalog = () => {
    const [books, setBooks] = useState([]);
    const [filteredBooks, setFilteredBooks] = useState([]);
    const [error, setError] = useState(null);
    const [sortOption, setSortOption] = useState('priceAsc'); // Сортировка по умолчанию по цене по возрастанию
    const [searchQuery, setSearchQuery] = useState(''); // Состояние для хранения поискового запроса

    useEffect(() => {
        fetch('http://localhost:5000/api/books') // API для получения списка книг
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Ошибка загрузки данных');
                }
                return response.json();
            })
            .then((data) => {
                setBooks(data);
                setFilteredBooks(data); // Изначально показываем все книги
            })
            .catch((err) => setError(err.message));
    }, []);

    // Функция для сортировки книг
    const sortBooks = (books, sortOption) => {
        switch (sortOption) {
            case 'priceAsc':
                return [...books].sort((a, b) => a.price - b.price); // Сортировка по цене по возрастанию
            case 'priceDesc':
                return [...books].sort((a, b) => b.price - a.price); // Сортировка по цене по убыванию
            case 'alphaAsc':
                return [...books].sort((a, b) => a.title.localeCompare(b.title)); // Сортировка по алфавиту (A-Z)
            case 'alphaDesc':
                return [...books].sort((a, b) => b.title.localeCompare(a.title)); // Сортировка по алфавиту (Z-A)
            default:
                return books;
        }
    };

    // Функция для фильтрации книг по названию
    const filterBooks = (books, query) => {
        if (!query) {
            return books;
        }
        return books.filter((book) =>
            book.title.toLowerCase().includes(query.toLowerCase())
        );
    };

    // Обрабатываем изменение поискового запроса
    const handleSearchChange = (e) => {
        const query = e.target.value;
        setSearchQuery(query);
        const filtered = filterBooks(books, query);
        setFilteredBooks(filtered);
    };

    if (error) {
        return <p className="error">{error}</p>;
    }

    const sortedBooks = sortBooks(filteredBooks, sortOption); // Применяем сортировку после фильтрации

    return (
        <div className="catalog">
            <h1>Books</h1>

            {/* Поле для поиска */}
            <div className="search">
                <label htmlFor="search">Поиск по названию:</label>
                <input
                    type="text"
                    id="search"
                    value={searchQuery}
                    onChange={handleSearchChange}
                    placeholder="Введите название книги..."
                />
            </div>

            {/* Селектор сортировки */}
            <div className="sort-options">
                <label htmlFor="sort">Сортировать по:</label>
                <select
                    id="sort"
                    value={sortOption}
                    onChange={(e) => setSortOption(e.target.value)}
                >
                    <option value="priceAsc">Цене (по возрастанию)</option>
                    <option value="priceDesc">Цене (по убыванию)</option>
                    <option value="alphaAsc">Алфавиту (A-Z)</option>
                    <option value="alphaDesc">Алфавиту (Z-A)</option>
                </select>
            </div>

            <div className="book-list">
                {sortedBooks.map((book) => (
                    <div className="book-card" key={book._id}>
                        <h2>{book.title}</h2>
                        <p>Автор: {book.author}</p>
                        <p>Цена: {book.price} руб.</p>
                        <p>{book.description}</p>
                        <Link to={`/book/${book._id}`} className="view-details-link">Подробнее</Link>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default Catalog;
