import React, { useState, useEffect } from 'react';
import './Home.css';

const Home = () => {
    const [weather, setWeather] = useState(null);
    const [books, setBooks] = useState([]);
    const [loading, setLoading] = useState(true);
    const currentDate = new Date();
    const formattedDate = currentDate.toLocaleDateString(undefined, {
        weekday: 'long',
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });
    const timeZone = Intl.DateTimeFormat().resolvedOptions().timeZone;

    // Fetch weather data
    useEffect(() => {
        const fetchWeather = async () => {
            try {
                const weatherResponse = await fetch(
                    `https://api.openweathermap.org/data/2.5/weather?q=London&appid=YOUR_API_KEY&units=metric`
                );
                const weatherData = await weatherResponse.json();
                setWeather(weatherData);
            } catch (error) {
                console.error("Error fetching weather data:", error);
            }
        };

        const fetchBooks = async () => {
            try {
                const booksResponse = await fetch(
                    `https://www.googleapis.com/books/v1/volumes?q=bestsellers&key=YOUR_API_KEY`
                );
                const booksData = await booksResponse.json();
                setBooks(booksData.items || []);
            } catch (error) {
                console.error("Error fetching books data:", error);
            } finally {
                setLoading(false);
            }
        };

        fetchWeather();
        fetchBooks();
    }, []);

    return (
        <div className="home">
            <h2>Welcome to the Bookstore</h2>
            <p>Explore our vast collection of books and find your next favorite read.</p>
            <p>Discover genres from fiction to non-fiction, and everything in between.</p>
            <div className="date-time-info">
                <p><strong>Current Date:</strong> {formattedDate}</p>
                <p><strong>Time Zone:</strong> {timeZone}</p>
            </div>
            <div className="weather-info">
                <h3>Current Weather</h3>
                {weather ? (
                    <p>
                        <strong>Location:</strong> {weather.name} <br />
                        <strong>Temperature:</strong> {weather.main.temp}°C <br />
                        <strong>Condition:</strong> {weather.weather[0].description}
                    </p>
                ) : (
                    <p>Loading weather data...</p>
                )}
            </div>
            <div className="books-info">
                <h3>Bestselling Books</h3>
                {loading ? (
                    <p>Loading books...</p>
                ) : (
                    <ul>
                        {books.map((book) => (
                            <li key={book.id}>
                                <strong>{book.volumeInfo.title}</strong> by{' '}
                                {book.volumeInfo.authors?.join(', ') || 'Unknown Author'}
                            </li>
                        ))}
                    </ul>
                )}
            </div>
        </div>
    );
};

export default Home;
