import React, { useState, useEffect } from 'react';
import axios from 'axios';

const CRUDPage = () => {
    const [books, setBooks] = useState([]); // Список книг
    const [isAuthenticated, setIsAuthenticated] = useState(false); // Статус авторизации
    const [currentBook, setCurrentBook] = useState(null); // Книга для редактирования
    const [formData, setFormData] = useState({
        title: '',
        author: '',
        genre: '',
        description: '',
        price: 0,
        stock: 0,
    });

    useEffect(() => {
        // Проверка статуса авторизации
        const token = localStorage.getItem('jwt');
        setIsAuthenticated(!!token); // Если токен есть, пользователь авторизован

        // Загрузка списка книг
        fetchBooks();
    }, []);

    const fetchBooks = async () => {
        try {
            const response = await axios.get('http://localhost:5000/api/books');
            setBooks(response.data);
        } catch (error) {
            console.error('Error fetching books:', error.message);
        }
    };

    const handleInputChange = (e) => {
        setFormData({
            ...formData,
            [e.target.name]: e.target.value,
        });
    };

    const handleAddOrEditBook = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem('jwt');
        const headers = { Authorization: `Bearer ${token}` };

        try {
            if (currentBook) {
                // Редактирование книги
                await axios.put(`http://localhost:5000/api/books/${currentBook._id}`, formData, { headers });
                setCurrentBook(null);
            } else {
                // Добавление новой книги
                await axios.post('http://localhost:5000/api/books', formData, { headers });
            }

            setFormData({ title: '', author: '', genre: '', description: '', price: 0, stock: 0 });
            fetchBooks();
        } catch (error) {
            console.error('Error saving book:', error.response?.data || error.message);
        }
    };

    const handleDeleteBook = async (id) => {
        const token = localStorage.getItem('jwt');
        const headers = { Authorization: `Bearer ${token}` };

        try {
            await axios.delete(`http://localhost:5000/api/books/${id}`, { headers });
            fetchBooks();
        } catch (error) {
            console.error('Error deleting book:', error.response?.data || error.message);
        }
    };

    const handleEditClick = (book) => {
        setCurrentBook(book);
        setFormData({
            title: book.title,
            author: book.author,
            genre: book.genre,
            description: book.description,
            price: book.price,
            stock: book.stock,
        });
    };

    return (
        <div>
            <h1>Список книг</h1>

            <ul>
                {books.map((book) => (
                    <li key={book._id}>
                        <strong>{book.title}</strong> — {book.author} ({book.genre})
                        {isAuthenticated && (
                            <>
                                <button onClick={() => handleEditClick(book)}>Редактировать</button>
                                <button onClick={() => handleDeleteBook(book._id)}>Удалить</button>
                            </>
                        )}
                    </li>
                ))}
            </ul>

            {isAuthenticated && (
                <div>
                    <h2>{currentBook ? 'Редактировать книгу' : 'Добавить книгу'}</h2>
                    <form onSubmit={handleAddOrEditBook}>
                        <input
                            type="text"
                            name="title"
                            placeholder="Название"
                            value={formData.title}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="text"
                            name="author"
                            placeholder="Автор"
                            value={formData.author}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="text"
                            name="genre"
                            placeholder="Жанр"
                            value={formData.genre}
                            onChange={handleInputChange}
                            required
                        />
                        <textarea
                            name="description"
                            placeholder="Описание"
                            value={formData.description}
                            onChange={handleInputChange}
                        ></textarea>
                        <input
                            type="number"
                            name="price"
                            placeholder="Цена"
                            value={formData.price}
                            onChange={handleInputChange}
                            required
                        />
                        <input
                            type="number"
                            name="stock"
                            placeholder="Количество"
                            value={formData.stock}
                            onChange={handleInputChange}
                            required
                        />
                        <button type="submit">{currentBook ? 'Сохранить изменения' : 'Добавить'}</button>
                    </form>
                </div>
            )}
        </div>
    );
};

export default CRUDPage;
